import{_ as s,o as a,c as e,R as t}from"./chunks/framework.333b51ca.js";const C=JSON.parse('{"title":"PostgreSQL justify_interval() 函数","description":"","frontmatter":{},"headers":[],"relativePath":"database/postgreSQL/ref/justify_interval.md","filePath":"database/postgreSQL/ref/justify_interval.md","lastUpdated":1690159250000}'),l={name:"database/postgreSQL/ref/justify_interval.md"},o=t('<h1 id="postgresql-justify-interval-函数" tabindex="-1">PostgreSQL justify_interval() 函数 <a class="header-anchor" href="#postgresql-justify-interval-函数" aria-label="Permalink to &quot;PostgreSQL justify_interval() 函数&quot;">​</a></h1><p>PostgreSQL <code>justify_interval()</code> 函数转换时间间隔的表示方法，将超过 24 小时的小时数转为天数，并且将超过 30 天的天数转为月数。</p><p><code>justify_interval()</code> 函数相当于 <a href="./justify_hours.html"><code>justify_hours()</code></a> 函数和 <a href="./justify_hours.html"><code>justify_days()</code></a> 函数的结合。</p><h2 id="justify-interval-语法" tabindex="-1"><code>justify_interval()</code> 语法 <a class="header-anchor" href="#justify-interval-语法" aria-label="Permalink to &quot;`justify_interval()` 语法&quot;">​</a></h2><p>这是 PostgreSQL <code>justify_interval()</code> 函数的语法：</p><div class="language-sql"><button title="Copy Code" class="copy"></button><span class="lang">sql</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#A6ACCD;">justify_interval(</span><span style="color:#F78C6C;">value</span><span style="color:#A6ACCD;"> INTERVAL) </span><span style="color:#89DDFF;">-&gt;</span><span style="color:#A6ACCD;"> INTERVAL</span></span></code></pre></div><h3 id="参数" tabindex="-1">参数 <a class="header-anchor" href="#参数" aria-label="Permalink to &quot;参数&quot;">​</a></h3><ul><li><p><em><code>value</code></em></p><p>必需的。 要转换的间隔值。</p></li></ul><h3 id="返回值" tabindex="-1">返回值 <a class="header-anchor" href="#返回值" aria-label="Permalink to &quot;返回值&quot;">​</a></h3><p>PostgreSQL <code>justify_interval()</code> 函数转换时间间隔的表示方法，将超过 24 小时的小时数转为天数，并且将超过 30 天的天数转为月数。</p><h2 id="justify-interval-示例" tabindex="-1"><code>justify_interval()</code> 示例 <a class="header-anchor" href="#justify-interval-示例" aria-label="Permalink to &quot;`justify_interval()` 示例&quot;">​</a></h2><p>下面的示例展示了 <code>justify_interval()</code> 函数的基本用法。</p><div class="language-sql"><button title="Copy Code" class="copy"></button><span class="lang">sql</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#F78C6C;">SELECT</span><span style="color:#A6ACCD;"> justify_interval(INTERVAL </span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">6000 hours</span><span style="color:#89DDFF;">&#39;</span><span style="color:#A6ACCD;">) </span><span style="color:#F78C6C;">AS</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">6000 hours</span><span style="color:#89DDFF;">&quot;</span><span style="color:#A6ACCD;">;</span></span></code></pre></div><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">6000</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">hours</span></span>\n<span class="line"><span style="color:#FFCB6B;">----------------</span></span>\n<span class="line"><span style="color:#A6ACCD;"> </span><span style="color:#FFCB6B;">8</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">mons</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">10</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">days</span></span></code></pre></div>',14),n=[o];function r(p,i,c,d,y,u){return a(),e("div",null,n)}const _=s(l,[["render",r]]);export{C as __pageData,_ as default};
