import{_ as a,o as e,c as s,R as r}from"./chunks/framework.333b51ca.js";const h=JSON.parse('{"title":"PostgreSQL trim() 函数","description":"","frontmatter":{},"headers":[],"relativePath":"database/postgreSQL/ref/trim_array.md","filePath":"database/postgreSQL/ref/trim_array.md","lastUpdated":1690296072000}'),o={name:"database/postgreSQL/ref/trim_array.md"},t=r('<h1 id="postgresql-trim-函数" tabindex="-1">PostgreSQL trim() 函数 <a class="header-anchor" href="#postgresql-trim-函数" aria-label="Permalink to &quot;PostgreSQL trim() 函数&quot;">​</a></h1><p>PostgreSQL <code>trim_array()</code> 函数从指定的数组的最后删除指定数量的元素并返回修改后的数组。</p><h2 id="trim-array-语法" tabindex="-1"><code>trim_array()</code> 语法 <a class="header-anchor" href="#trim-array-语法" aria-label="Permalink to &quot;`trim_array()` 语法&quot;">​</a></h2><p>这里是 PostgreSQL <code>trim_array()</code> 函数的语法：</p><div class="language-sql"><button title="Copy Code" class="copy"></button><span class="lang">sql</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#A6ACCD;">trim_array(</span><span style="color:#F78C6C;">array</span><span style="color:#A6ACCD;">, </span><span style="color:#C792EA;">number</span><span style="color:#A6ACCD;">) </span><span style="color:#89DDFF;">-&gt;</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">array</span></span></code></pre></div><h3 id="参数" tabindex="-1">参数 <a class="header-anchor" href="#参数" aria-label="Permalink to &quot;参数&quot;">​</a></h3><ul><li><p><em><code>array</code></em></p><p>必需的。 要从中删除元素的数组。</p></li><li><p><em><code>number</code></em></p><p>必需的。 要删除的元素的数量。</p></li></ul><h3 id="返回值" tabindex="-1">返回值 <a class="header-anchor" href="#返回值" aria-label="Permalink to &quot;返回值&quot;">​</a></h3><p>PostgreSQL <code>trim_array()</code> 函数从数组 <code>array</code> 的最后删除 <code>number</code> 个元素，并返回删除元素后的数组。</p><p>如果指定的数组为 <code>NULL</code>，<code>trim_array()</code> 函数将返回 <code>NULL</code>。</p><h2 id="trim-array-示例" tabindex="-1"><code>trim_array()</code> 示例 <a class="header-anchor" href="#trim-array-示例" aria-label="Permalink to &quot;`trim_array()` 示例&quot;">​</a></h2><p>本示例展示了如何使用 PostgreSQL <code>trim_array()</code> 函数从数组 <code>{1, 2, 3, 4, 5, 6}</code> 删除最后 3 个元素。</p><div class="language-sql"><button title="Copy Code" class="copy"></button><span class="lang">sql</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#F78C6C;">SELECT</span><span style="color:#A6ACCD;"> trim_array(</span><span style="color:#F78C6C;">ARRAY</span><span style="color:#A6ACCD;">[1, 2, 3, 4, 5, 6], </span><span style="color:#F78C6C;">3</span><span style="color:#A6ACCD;">);</span></span></code></pre></div><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">trim_array</span></span>\n<span class="line"><span style="color:#FFCB6B;">------------</span></span>\n<span class="line"><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">{</span><span style="color:#FFCB6B;">1,2,3}</span></span></code></pre></div>',14),l=[t];function n(p,c,i,d,m,y){return e(),s("div",null,l)}const C=a(o,[["render",n]]);export{h as __pageData,C as default};
